import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
//import "../style/MoodResults.css";

const apikey = "a4bd042c"; // Keep OMDB API keys in environment variables for security
const openAiApiKey = "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";

const MoodResults = () => {
    const { mood } = useParams();
    const [movies, setMovies] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                // Step 1: Get movie recommendations from ChatGPT based on the selected mood
                const moodRecommendations = await getMoodBasedRecommendationsFromChatGPT(mood);

                // Step 2: Fetch movie details using OMDB API for those recommendations
                const movieDetails = await fetchMovieDetails(moodRecommendations);
                setMovies(movieDetails);
            } catch (error) {
                console.error("Error fetching mood-based movies:", error);
            }
        };

        fetchMovies();
    }, [mood]);

    // Step 1: Fetch mood-based movie recommendations using ChatGPT
    // Step 1: Fetch mood-based movie recommendations using ChatGPT
const getMoodBasedRecommendationsFromChatGPT = async (mood) => {
    try {
       const prompt = `Give me some movie recommendations for a ${mood} mood.`;
       const userInput = `Provide me with a list of movies that are good for a ${mood} mood. Do not respond with anything other than a list of movie names, don't add numbers, just the movie names.`;

       const result = await axios.post(
          'https://api.openai.com/v1/chat/completions',
          {
             model: 'gpt-3.5-turbo',
             messages: [
                { role: 'system', content: 'You are a movie recommendation assistant.' },
                { role: 'user', content: userInput },
             ],
          },
          {
             headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${openAiApiKey}`,
             },
          }
       );

       // Get the movie recommendations from ChatGPT and clean it up
       const recommendationsString = result.data.choices[0].message.content;

       // Split the movie titles by newline or comma, remove numerical prefixes, and clean up extra spaces
       const recommendations = recommendationsString
          .split(/\r?\n|\r|,/)
          .map((title) => title.trim())  // Remove extra spaces
          .map((title) => title.replace(/^\d+\.\s*/, ''))  // Remove leading number and dot (e.g., "1. ")
          .filter(Boolean); // Remove empty entries

       console.log("Cleaned movie recommendations:", recommendations);
       return recommendations;
    } catch (error) {
       console.error('Error getting mood-based recommendations from ChatGPT:', error);
       throw error;
    }
};



const cleanMovieTitle = (title) => {
    // Strip any extra descriptions, years, or punctuation, and remove leading hyphen if present
    const match = title.match(/^[a-zA-Z0-9\s]+/);  // Match only alphanumeric characters and spaces
    return match ? match[0].replace(/^-/, '').trim() : title;  // Remove leading hyphen and trim spaces
};


    const fetchMovieDetails = async (movies) => {
        if (!Array.isArray(movies)) {
            console.error("Movies is not an array:", movies);
            return [];
        }

        const movieDetailsPromises = movies.map(async (movie) => {
            const cleanedMovie = cleanMovieTitle(movie);
            console.log("Searching for movie:", cleanedMovie);  // Log the search query
            const response = await axios.get(
                `http://www.omdbapi.com/?apikey=${apikey}&t=${cleanedMovieTitle}`
            );
            return response.data?.Title ? response.data : null;
        });

        const movieDetails = await Promise.all(movieDetailsPromises);
        return movieDetails.filter((movie) => movie !== null);
    };



    return (
        <div className="mood-results-container">
            <h2>Movies with mood: {mood}</h2>
            <div className="movies-list">
                {movies.map((movie) => (
                    <div
                        key={movie.imdbID}
                        className="movie-card"
                        onClick={() => navigate(`/moviesDetails/${movie.imdbID}`)}
                    >
                        <img src={movie.Poster} alt={movie.Title} />
                        <h3>{movie.Title}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MoodResults;
