import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // For API requests
import "../style/HomePage.css"; // Assuming your styles are already set

const HUGGING_FACE_API_URL = "https://api-inference.huggingface.co/models/UmeshSaini/IMDB_rating_of_movie";
const API_TOKEN = "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"; // Replace with your Hugging Face token

const Home = ({ setSearchTerm, setMovies, setCurrentPage, setTotalResults }) => {
    const [input, setInput] = useState(""); // User input
    const [messages, setMessages] = useState([]); // Chat history
    const navigate = useNavigate(); // Navigation for your app

    // Function to handle chatbot messages
    const handleSendMessage = async () => {
        if (!input.trim()) return;

        // Append user's message to the chat
        const userMessage = { user: "user", text: input };
        setMessages((prevMessages) => [...prevMessages, userMessage]);

        try {
            const response = await axios.post(
                HUGGING_FACE_API_URL,
                { inputs: input },
                {
                    headers: {
                        Authorization: `Bearer ${API_TOKEN}`,
                        "Content-Type": "application/json",
                    },
                }
            );
            console.log("Full response data:", response.data); // Log full response to inspect it
            const botMessage = response.data.generated_text || response.data[0]?.generated_text || "No response received.";
            setMessages((prevMessages) => [...prevMessages, { user: "bot", text: botMessage }]);
        } catch (error) {
            console.error("Error communicating with Hugging Face API:", error);
            setMessages((prevMessages) => [
                ...prevMessages,
                { user: "bot", text: "Failed to get a response. Please try again." },
            ]);
        }
        

        setInput(""); // Clear input field
    };

    // Your existing handleSubmit for OMDB API
    const handleSubmit = async (e) => {
        e.preventDefault();
        setSearchTerm(input);
        setCurrentPage(1);

        try {
            const response = await axios.get(`http://www.omdbapi.com/?apikey=a4bd042c&s=${input}&page=1`);
            setMovies(response.data.Search || []);
            setTotalResults(response.data.totalResults);
            navigate("/searchResults");
        } catch (error) {
            console.error("Error fetching movies from OMDB API:", error);
        }
    };

    return (
        <div className="home-container">
            <h1>Every movie has a story, and every story finds its home here at Movie Home</h1>

            {/* Movie Search Form */}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Search for Movies"
                />
                <button type="submit">Search</button>
            </form>

            {/* Chat Section */}
            <div className="chat-container" style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
                <h2>Chat with Hugging Face</h2>
                <div
                    className="chat-messages"
                    style={{ height: "200px", overflowY: "auto", marginBottom: "10px", padding: "5px" }}
                >
                    {messages.map((msg, index) => (
                        <div
                            key={index}
                            style={{
                                textAlign: msg.user === "bot" ? "left" : "right",
                                margin: "5px 0",
                                color: msg.user === "bot" ? "blue" : "green",
                            }}
                        >
                            <strong>{msg.user === "bot" ? "Bot: " : "You: "}</strong>
                            {msg.text}
                        </div>
                    ))}
                </div>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Type a message"
                    style={{ width: "100%", marginBottom: "10px", padding: "5px" }}
                />
                <button onClick={handleSendMessage} style={{ width: "100%", padding: "10px" }}>
                    Send to Chatbot
                </button>
            </div>
        </div>
    );
};

export default Home;
