import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../style/HomePage.css";

const apikey = "a4bd042c";
const openAiApiKey = "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";

const Home = ({ setSearchTerm, setMovies, setCurrentPage, setTotalResults }) => {
    const [input, setInput] = useState("");
    const [moodButtonsVisible, setMoodButtonsVisible] = useState(false); 
    const [selectedMood, setSelectedMood] = useState(""); 
    const [movieResults, setMovieResults] = useState([]); // State to store movie results
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSearchTerm(input);
        setCurrentPage(1);
        try {
            const response = await axios.get(
                `http://www.omdbapi.com/?apikey=${apikey}&s=${input}&page=1`
            );
            setMovies(response.data.Search || []);
            setTotalResults(response.data.totalResults || 0);
            navigate("/searchResults");
        } catch (error) {
            console.error("Error fetching movies:", error);
            alert("Failed to fetch movies. Please try again.");
        }
    };

    const handleChatGPTQuery = async (query) => {
        try {
            const response = await axios.post(
                "https://api.openai.com/v1/chat/completions",
                {
                    model: "gpt-3.5-turbo",
                    messages: [{ role: "user", content: query }],
                },
                {
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${openAiApiKey}`,
                    },
                }
            );

            const chatResponse = response.data.choices?.[0]?.message?.content?.trim();
            if (!chatResponse) {
                throw new Error("No valid response received from ChatGPT.");
            }
            return chatResponse;
        } catch (error) {
            console.error("Error interacting with ChatGPT API:", error);
            alert("Failed to get a response from ChatGPT. Please try again.");
            return "No suggestion available.";
        }
    };

    const handleMoodSelection = async (mood) => {
        setSelectedMood(mood);
        setMoodButtonsVisible(false); // Hide mood buttons after selection
        try {
            const moodQuery = `Recommend some movies for a ${mood} mood.`;
            const chatResponse = await handleChatGPTQuery(moodQuery);

            console.log("ChatGPT Mood Response:", chatResponse); 

            const movieTitles = chatResponse.split('\n').map(title => title.trim()).filter(title => title);
            if (movieTitles.length === 0) {
                alert("No movie suggestions found.");
                return;
            }

            const moviePromises = movieTitles.map(title => 
                axios.get(`http://www.omdbapi.com/?apikey=${apikey}&t=${title}`)
            );
            const movieResponses = await Promise.all(moviePromises);

            const movies = movieResponses.map(response => response.data);
            setMovieResults(movies); // Set the fetched movie results here
        } catch (error) {
            console.error("Error fetching mood-based movies:", error);
            alert("Failed to get movie recommendations for the selected mood.");
        }
    };

    const handleRandomMovie = async () => {
        const randomWords = ["love", "war", "life", "future", "star", "hero"];
        const randomWord = randomWords[Math.floor(Math.random() * randomWords.length)];
        try {
            const response = await axios.get(
                `http://www.omdbapi.com/?apikey=${apikey}&s=${randomWord}`
            );
            const movies = response.data.Search;
            if (movies && movies.length > 0) {
                const randomMovie = movies[Math.floor(Math.random() * movies.length)];
                navigate(`/moviesDetails/${randomMovie.imdbID}`, {
                    state: { movie: randomMovie, searchTerm: randomWord, movies: movies },
                });
            } else {
                alert("No random movie found. Try again.");
            }
        } catch (error) {
            console.error("Error fetching random movie:", error);
            alert("Failed to fetch a random movie. Try again.");
        }
    };

    return (
        <div className="home-container">
            <h1>Every movie has a story, and every story finds its home here at Movie Home</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Search for Movies"
                />
                <button type="submit">Search</button>
            </form>

            <button onClick={() => setMoodButtonsVisible(!moodButtonsVisible)} className="mood-button">
                Movies that match your mood
            </button>

            {moodButtonsVisible && (
                <div className="mood-buttons">
                    <button onClick={() => handleMoodSelection("happy")}>Happy</button>
                    <button onClick={() => handleMoodSelection("sad")}>Sad</button>
                    <button onClick={() => handleMoodSelection("lonely")}>Lonely</button>
                    <button onClick={() => handleMoodSelection("excited")}>Excited</button>
                    <button onClick={() => handleMoodSelection("angry")}>Angry</button>
                </div>
            )}

            {/* Display movie results here */}
            {movieResults.length > 0 && (
                <div className="movie-results">
                    {movieResults.map((movie, index) => (
                        <div className="movie-card" key={index}>
                            <img src={movie.Poster} alt={movie.Title} />
                            <h3>{movie.Title}</h3>
                            <p>{movie.Year}</p>
                            <button onClick={() => navigate(`/moviesDetails/${movie.imdbID}`)}>
                                View Details
                            </button>
                        </div>
                    ))}
                </div>
            )}

            <button onClick={handleRandomMovie} className="random-movie-button">
                Random Movie
            </button>
        </div>
    );
};

export default Home;





