import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../style/MoodResults.css";

const apikey = "bf639062"; // OMDB API Key
const openAiApiKey = "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"; 

const MoodResults = () => {
    const { mood } = useParams(); // Get mood from URL
    const navigate = useNavigate();
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                console.log("Fetching titles from ChatGPT...");

                // ChatGPT prompt
                const prompt = `You are a movie recommendation assistant. Provide a list of 10 movie titles matching the mood "${mood}". Respond with only titles, no numbering, no special characters, and ensure one title per line.`;

                // Call ChatGPT API
                const openaiResponse = await axios.post(
                    "https://api.openai.com/v1/chat/completions",
                    {
                        model: "gpt-3.5-turbo",
                        messages: [
                            { role: "system", content: prompt },
                            { role: "user", content: mood },
                        ],
                    },
                    {
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: `Bearer ${openAiApiKey}`,
                        },
                    }
                );

                // Extract movie titles
                const rawTitles = openaiResponse.data.choices[0].message.content.split("\n");
                const titles = rawTitles
                    .map((title) => title.trim()) // Remove extra spaces
                    .filter((title) => title && title.length > 0) // Remove empty or invalid entries
                    .slice(0, 10); // Limit to 10 titles

                console.log("Fetched titles:", titles);

                // Fetch movie details from OMDB
                console.log("Fetching movies from OMDB...");
                const moviePromises = titles.map((title) =>
                    axios.get(`http://www.omdbapi.com/?apikey=${apikey}&t=${encodeURIComponent(title)}`)
                );

                const movieResponses = await Promise.all(moviePromises);
                const movieData = movieResponses
                    .map((response) => response.data)
                    .filter((movie) => movie && movie.Response !== "False"); // Exclude invalid responses

                setMovies(movieData);
            } catch (err) {
                console.error("Error fetching mood-based movies:", err);
                setError("Failed to fetch movies. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        fetchMovies();
    }, [mood]);


    if (loading) {
        return <span className="loader">Loading...</span>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="mood-results-container">
            <h1>Movies for the mood: {mood}</h1>
            <div className="movies-grid">
                {movies.map((movie) => (
                    <div key={movie.imdbID} className="movie-card">
                        <img
                            src={movie.Poster !== "N/A" ? movie.Poster : "default-poster.jpg"}
                            alt={movie.Title}
                        />
                        <h2>{movie.Title}</h2>
                        <p><strong>Year:</strong> {movie.Year}</p>
                        <p><strong>Genre:</strong> {movie.Genre}</p>
                        <button onClick={() => navigate(`/moviesDetails/${movie.imdbID}`)}>
                            View Details
                        </button>
                    </div>
                ))}
            </div>
            <button onClick={() => navigate("/")}>Back to Home</button>
        </div>
    );
};

export default MoodResults;
