import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";

const apikey = "a4bd042c";  // Keep API keys in environment variables for security
const openAiApiKey = process.env.OPENAI_API_KEY;  // Use environment variables for sensitive data

const MoodResults = () => {
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const { state } = useLocation();
    const { mood } = state || {};  // Get the mood from state

    useEffect(() => {
        if (mood) {
            handleMoodSelection(mood);
        }
    }, [mood]);

    const sanitizeTitle = (title) => {
        return title.replace(/^\d+\.\s*/, "").trim();
    };

    const handleChatGPTQuery = async (query) => {
        try {
            const response = await axios.post(
                "https://api.openai.com/v1/chat/completions",
                {
                    model: "gpt-3.5-turbo",
                    messages: [{ role: "user", content: query }],
                },
                {
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${openAiApiKey}`,
                    },
                }
            );

            const chatResponse = response.data?.choices?.[0]?.message?.content?.trim();
            console.log("ChatGPT Response:", chatResponse);

            if (!chatResponse) {
                throw new Error("No response from ChatGPT.");
            }

            const movieTitles = chatResponse
                .split("\n")
                .map((title) => title.trim())
                .filter((title) => title);

            return movieTitles;
        } catch (error) {
            console.error("Error interacting with ChatGPT API:", error);
            alert("Failed to get a response from ChatGPT. Please try again.");
            return [];
        }
    };

    const handleMoodSelection = async (mood) => {
        setLoading(true);
        try {
            const moodQuery = `Give me a list of movie names for someone feeling ${mood}. Please provide only movie names, separated by a comma or newline.`;
            const movieTitles = await handleChatGPTQuery(moodQuery);

            if (movieTitles.length === 0) {
                alert("No movie suggestions found.");
                setLoading(false);
                return;
            }

            const moviePromises = movieTitles.map((title) => {
                const sanitizedTitle = sanitizeTitle(title);
                return axios.get(`http://www.omdbapi.com/?apikey=${apikey}&t=${sanitizedTitle}`);
            });

            const movieResponses = await Promise.all(moviePromises);

            const movies = movieResponses
                .map((response) => response.data)
                .filter((movie) => movie.Poster && movie.Title && movie.Year);

            setMovies(movies);
        } catch (error) {
            console.error("Error fetching mood-based movies:", error);
            alert("Failed to get movie recommendations for the selected mood.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="movie-results-container">
            {loading ? (
                <p>Loading movies...</p>
            ) : movies.length > 0 ? (
                <div className="movie-results">
                    {movies.map((movie) => (
                        <div className="movie-card" key={movie.imdbID}>
                            <a href={`/moviesDetails/${movie.imdbID}`}>
                                <img src={movie.Poster} alt={movie.Title} />
                                <h3>{movie.Title}</h3>
                                <p>{movie.Year}</p>
                            </a>
                        </div>
                    ))}
                </div>
            ) : (
                <p>No movies found for the selected mood. Try again!</p>
            )}
        </div>
    );
};

export default MoodResults;
